#define max(a,b) \
 ({ __typeof__ (a) _a = (a); \
     __typeof__ (b) _b = (b); \
   _a > _b ? _a : _b; })

#define NELEMS(x)  (sizeof(x) / sizeof(x[0]))

#include <stdlib.h>
#include <stdio.h>

void tweets_per_second(int *tps, int tps_length, int k) {
    int i, j, v, win_l;
    int max_val = tps[0];
    int max_pos = 0;
    for (i=0; i < tps_length; i++) {
        win_l = max(0, i - k + 1);
        v = tps[i];

        // // print window
        // for (int _i=win_l; _i<=i; _i++){
        //   printf("%d ", tps[_i]);
        // }
        // printf(" \n");

        if (max_val < v || max_pos < win_l) {
            for (j=win_l, max_val=tps[j]; j<=i; j++) {
                if (tps[j] >= max_val) {
                    max_val = tps[j];
                    max_pos = j;
                }
            }
        }
        printf("%d\n", max_val, max_pos);
    }
}

int main(void) {
  tweets_per_second(tps, NELEMS(tps), k);
}